#include&lt;vector&gt;

int main()
{
    std::vector&lt;int&gt; vec;
    int n;
    std::cin &gt;&gt; n;
    while (n--) {
        int x;
        std::cin &gt;&gt; x;
        vec.push_back(x);
    }
    long long sum = 0;
    for(auto i : vec) {
        std::cout &lt;&lt; i &lt;&lt; ' ';
        sum += i;
        sum %= 998244353;
    }
    std::cout &lt;&lt; sum &lt;&lt; '\n';
    for(auto it = vec.rbegin(); it != vec.rend(); ++it)
        std::cout &lt;&lt; *it &lt;&lt; ' ';
    return 0;
}
