#include&lt;vector&gt;
#include&lt;random&gt;

int main()
{
    std::random_device device;
    unsigned int seed = device();
    std::mt19937 engine(seed);
    std::vector&lt;int&gt; vec;
    int n, m;
    std::cin &gt;&gt; n;
    m = n;
    while (n--) {
        int x;
        std::cin &gt;&gt; x;
        vec.push_back(x);
    }
    long long sum = 0;
    for(int _ = 0; _ &lt; m ; ++_) {
        auto i = vec[engine() % m];
        sum += i;
        sum %= 998244353;
    }
    std::cout &lt;&lt; sum;
    return 0;
}
